import React, { useEffect, useState } from 'react';
import { Link, useHistory, useParams } from 'react-router-dom';
import { updateCard, readCard } from '../../utils/api';
import DeckShape from './DeckShape';

export default function EditCard() {
    const [ card, setCard ] = useState({ fron: '', back: '' })
    const history = useHistory();
    const { cardId } = useParams();

    useEffect(() => {
        readCard(cardId)
        .then(data => setCard(data))
    },[cardId]);

    function submitHandler(updatedDeck) {
        updateCard(updatedCard).then(() => {
            history.push(`/decks/${deckId}`)
        })
    }

    function cancel() {
        history.goBack();
    }

    const editForm = deck.id ? (
        <CardShape onCancel={cancel} onSubmit={submitHandler} initialState={card} />
    ) : (
        <h5>Loading...</h5>
    )

    return (
        <div>
          <nav aria-label='breadcrumb'>
            <ol className='breadcrumb'>
              <li className='breadcrumb-item'>
                <Link to='/'>
                  <span className='oi oi-home' /> Home
                </Link>
              </li>
              <li className='breadcrumb-item active' aria-current='page'>
                Edit Card
              </li>
            </ol>
          </nav>
          <h3>Edit Card</h3>
          {editForm}
        </div>
    );
}
