import { deleteCard, listCards } from '../../utils/api';
import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';

export default function CardList() {
    const [ cardList, setCardList] = useState([]);
    const { deckId } = useParams();

    useEffect(() => {
        listCards()
        .then(setCardList)
    },[deckId])

    const list = cardList.map((card) => {
        <li key={card.id} className="border list-group-item list-group-item-action flex-column align-items-start">
        <div className="d-flex w-100 justify-content-between">
          <h3 className="mb-1">{card.name}</h3>
          {/* <small>{deck.cards.length} cards</small> */}
        </div>
        <p className="mb-1">{deck.description}</p>

        <div className="btn-group">
          <Link to={`decks/${deck.id}`} className="btn btn-secondary"><span />View</Link>
          <Link to={`decks/${deck.id}/study`} className="btn btn-primary"><span />Study</Link>
          <Link to="" className="btn btn-danger float-right" id={deck.id} onClick={() => deleteHandler(deck.id)}>
            <span className="oi oi-trash"/>
          </Link>
        </div>
      </li>
    })

    return (
        <div>

        </div>
    )
}
